ered'];				        ?>          <tr>            <td data-title="Jobcode"><?php echo getJobDetails($conn,$row['job_id'],'job_code');?></td>            <td data-title="Candidate Name"><?php echo getUserName($conn,$row['registration_id']);?> </td>            <td data-title="Contact"><?php echo getUserMobile($conn,$row['registration_id']);?></td>            <!--<td data-title="JobbReady Score">85%</td>-->            <!--<td data-title=""><a href="#">Contacted</a></td>-->			<td data-title=""><?php if($candidate_interviewed == "Y") { ?><a href="#" class="contact">Interviewed</a><?php } else { ?><a href="#" class="contact candidatecontact <?php echo $row['id'];?>">Contact</a> <?php } ?>			</td>			<td data-title="offer"><?php if($candidate_offered == "Y") { ?><a href="#" class="contact">Done</a><?php } else { ?><a class="contact fancybox fancybox.ajax fade" href="employer_offered_inc.php?uid=<?php echo $row['id'];?>">Offer</a> <?php } ?>			</td>			<!--<td data-title="Offer"><a class="fancybox fancybox.ajax fade" href="employer_offered_inc.php?uid=<?php echo $row['id'];?>">Offer</a></td>-->          </tr>          <?php }?>        </tbody>      </table>      <div class="clear"></div>    </div>
